#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){    int n,k;cin>>n>>k;    vector<int>a(n),b(n);    for(auto &i:a)cin>>i;    for(auto &i:b)cin>>i;    int ans=0;    int maxx=0;    int sum=0;    for(int i=0;i<min(n,k);i++){        sum+=a[i];        maxx=max(b[i],maxx);        ans=max(ans,sum+maxx*(k-i-1));    }     cout<<ans<<nl;       }signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}