#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){int n,k;cin>>n>>k;string s;cin>>s; if(n==k){    for(int i=0;i<n;i++)cout<<"-";        cout<<nl;    return; }  int a = count(s.begin(), s.end(), '0');  int b = count(s.begin(), s.end(), '1');  int c = count(s.begin(), s.end(), '2');  int maxa=a+c;  int maxb=b+c;   for(int i=1;i<=n;i++){    if(i<=a || i> n-b)cout<<'-';    else if(i<=maxa || i> n-maxb)cout<<"?";    else cout<<"+";   }  cout<<endl;  } signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}