#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){string s;cin>>s;int n=s.size();// cout<<s<<nl;int left=0,right=s.size()-1;while(left<n && s[left]=='<')left++;while(right>=0 && s[right]=='>')right--;if(right-left+1>=2){    cout<<"-1"<<nl;    return;}int ans=0;ans=max(left,n-right-1);if(right-left+1==1)ans++; cout<<ans<<nl;} signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);     int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}