#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){int n,k;cin>>n>>k;string s;cin>>s;int result=0;vector<bool>ans(n,1);for(int i=0;i<n;i++){if(s[i]=='1'){    ans[i]=0;    for(int j=1;j<=k;j++){        if(i+j<n){            ans[i+j]=0;        }    } }else {continue;} }// for(int i=0;i<n;i++)cout<<ans[i]<<" ";// cout<<endl;for(int i=0;i<n;i++){     if(ans[i])result++; }cout<<result<<nl;}signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}