#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10;Â void solve(){int n;cin>>n;vector<int>a(n),b(n);for(auto &i:a)cin>>i;for(auto &i:b)cin>>i;int maxx=0;int minn=0;for(int i=0;i<n;i++){    int v1=maxx-a[i];    int v2=b[i]-maxx;    int v3=minn-a[i];    int v4=b[i]-minn;    maxx=max({v1,v2,v3,v4});    minn=min({v1,v2,v3,v4});}cout<<maxx<<nl;}signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);     int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}