#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){   int n;cin>>n;  string s;cin>>s;   string t=s+s;  int maxx0=0;  int curr0=0;  for(auto c:t){    if(c=='0')curr0++;    else{        maxx0=max(maxx0,curr0);        curr0=0;    }  }  maxx0=max(maxx0,curr0);  if(maxx0>n)maxx0=n;  cout<<maxx0<<nl;}signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}