#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){   int n;cin>>n;vector<int>even,odd;int sum=0;for(int i=0;i<n;i++){    int x;cin>>x;    sum+=x;    if(x&1)odd.push_back(x);    else even.push_back(x);}sort(even.begin(),even.end());sort(odd.begin(),odd.end()); if(even.size()==n){    for(int i=0;i<n;i++)cout<<0<<" ";        cout<<nl;    return;}int m=odd.size();if(odd.size()==n){    for(int i=1;i<=n;i++){        if(i&1)cout<<odd[m-1]<<" ";        else cout<<0<<" ";              }    cout<<nl;     return;} int score=odd.back();vector<int>ans(n+1);ans[1]=score;for(int k=2;k<=n;k++){    if(even.size()==0)ans[k]=ans[k-2];    else{        ans[k]=ans[k-1]+even.back();        even.pop_back();    }}if(sum&1^1){    ans[n]=0;} for(int i=1;i<=n;i++){    cout<<ans[i]<<" ";}cout<<nl;}signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}