#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10;Â void solve(){    int n;cin>>n;    vector<int>a(n);    for(auto &i:a)cin>>i;    int sum=0;    for(int i=0;i<n-1;i++){        sum+=abs(a[i]-a[i+1]);    }    int minn_ans=sum;    minn_ans=min(minn_ans,sum-abs(a[0]-a[1]));    minn_ans=min(minn_ans,sum-abs(a[n-2]-a[n-1]));    for(int i=1;i<n-1;i++){        int remove=abs(a[i]-a[i-1])+abs(a[i+1]-a[i]);        int add=abs(a[i+1]-a[i-1]);        minn_ans=min(minn_ans,sum-remove+add);    }    cout<<minn_ans<<nl;}signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}