#include<bits/stdc++.h>using namespace std;#define nl "\n"#define int long long #define pb push_backint mod=1e9+7;int gcd(int a,int b){if(b==0)return a;return gcd(b,a%b);}const int inf=1e18;const int N=1e5+10; void solve(){int n,x,y;cin>>n>>x>>y;string s;cin>>s;vector<int>v(n);int sum=0;for(int i=0;i<n;i++){    cin>>v[i];    sum+=v[i];}if(sum>x+y){    cout<<"NO"<<nl;    return;}int a=0;int b=0;bool flag1=false,flag2=false;for(int i=0;i<n;i++){    if(s[i]=='0'){        a+=v[i]/2+1;        flag1=true;     }else{        b+=v[i]/2+1;        flag2=true;    }}if(a>x ||b>y)cout<<"NO"<<nl;else{    if(flag1 && flag2){        cout<<"YES\n";    }else if(flag1){        if(x>=y+n)cout<<"YES\n";        else cout<<"NO\n";    }else{        if(y>=x+n)cout<<"YES\n";        else cout<<"NO\n";    }}    }signed main(){    ios_base::sync_with_stdio(0);    cin.tie(0);cout.tie(0);    int t=1;    cin>>t;    while(t--){        solve();    }    return 0;}